        console.log(getPathName(pathname, menu))
        // setMenuName(getPathName(pathname, menu))
    }, [])
    return (
        <Layout>
            <AppAside menu={menus}></AppAside>
            <Layout>
                <AppHeader menuName={menuName}/>
            </Layout>
        </Layout>
    )
}

export default withRouter(DefaultLayout)
